import React from 'react'
import { Form, FormControl} from 'react-bootstrap';

function TodoForm({ todos , setTodos , input , setInput, isEditing , setIsEditing , editId }) {

    
    const handelChange = (e) => {
        setInput(e.target.value);
    }

    const handleSubmit = (e) => {

        if(isEditing){
            e.preventDefault();
            const todo = todos.map((todo)=>{
                if(todo.id === editId){
                    return {
                        id: todo.id,
                        text: input,
                        backgroundColor: todo.backgroundColor
                    }
                }
                return todo;
            })

            setTodos(todo)
            setInput('');
            setIsEditing(false);

        }
        else{

            e.preventDefault();
    
            const randomNumber = Math.random().toString(16).substr(-6);
            console.log(randomNumber);
            const todo = {
                id: Date.now(),
                text: input,
                backgroundColor: randomNumber
            }
    
            addTodo(todo);
            setInput('');
        }
    
    }
    const addTodo = (todo) => {
        if(!todo.text || /^\s*$/.test(todo.text)) {
            return;
        }

        const newTodos = [todo, ...todos];
        setTodos(newTodos);
    }

  return (
    <div className='d-flex justify-content-center' onSubmit={handleSubmit}>
        <Form className='d-flex w-50'>
            <FormControl value={input} onChange={handelChange} className='border-0 bg-transparent text-white' type="text" placeholder="Add a todo"/>
            <button className='btn btn-primary'>{isEditing ? "Edit" : "Add"}</button>
        </Form>
    </div>
  )
}

export default TodoForm
