import React from 'react';


import TodoForm from '../TodoForm';
import TodoList from './../TodoList/index';

const MyCard = () => {
    const [input, setInput] = React.useState('');
    const [todos, setTodos] = React.useState([]);
    const[isEditing, setIsEditing] = React.useState(false);
    const[editId, setEditId] = React.useState(null);


return (
    <>
            <Card className='justify-content-between col-6 p-4 mt-3'>
                <CardHeader>
                    <div className='text-white text-center'>
                        <h3>Feelin' productive today?</h3>
                    </div>
                    <TodoForm todos={todos} setTodos={setTodos} input={input} setInput={setInput} isEditing={isEditing} editId={editId} setIsEditing={setIsEditing} />
                </CardHeader>
                <Card.Body className='pt-5'>
                    <TodoList setInput={setInput} todos={todos} setTodos={setTodos} setIsEditing={setIsEditing} isEditing={isEditing} setEditId={setEditId}/>
                </Card.Body>
            </Card>
        </>
    );
}

export default MyCard;
