import { Container } from '@mui/material';
import React from 'react'
import { Box } from '@mui/system';

function Todo({ textTodo , idTodo, todoBg , setTodos , todos , setInput ,setIsEditing , setEditId}) {


    const handleDelete = (id) => {
        const newTodos = todos.filter(todo => todo.id !== id);
        setTodos(newTodos);
        console.log(todos)
    }
    
    const handleEdit = (id) => {
        setIsEditing(true);
        setEditId(id);
        const todo = todos.find(todo => todo.id === id);
        setInput(todo?.text || ''); 
    }


  return (
    <Container className="my-2">
         <Box components={"section"} >
            <Col className="p-2 rounded-3 d-flex justify-content-between align-items-center" style={{
            backgroundImage: `linear-gradient(to left, ${"#" + todoBg} 100%,  ${"#" + todoBg} 50%, ${"#" + todoBg} 0% )`,
            }}>
                <div className="fs-5">
                    {textTodo}
                </div>
                <div>
                    <TiDeleteOutline onClick={() => handleDelete(idTodo)} className='btn icon-delete p-0'/>
                    <FiEdit onClick={() => handleEdit(idTodo)} className='btn icon p-0'/>
                </div>
            </Col>
        </Box>
    </Container>
  )
}

export default Todo
